mapper.select:
	select 
		date_format(A.paymentApprovalDateTime, '%Y-%m-%d') as searchDate,
   	 		count(A.paymentApprovalAmount) as payCase,
   	 		sum(if(A.paymentApprovalAmount >= 0, A.paymentApprovalAmount , 0)) as salesApprovalSum,
   	 		 
			sum(if(paymentApprovalAmount < 0, paymentApprovalAmount , 0)) as salesCancelSum,
			
   	 		sum(A.paymentApprovalAmount) as salesSum
	from 
		(
		 select * from payment_transaction where 1= 1
			 and (paymentApprovalDateTime between #{searchDateStart} and #{searchDateEnd})
			and affiliateNo = #{searchAffiliate}
			and paymentTransactionType = #{searchPaymentTransactionType}
			and paymentApprovalStatus = #{searchPaymentApprovalStatus}
			
      			AND ( memberName LIKE CONCAT('%',#{searchKeyword,jdbcType=VARCHAR},'%' ) OR memberEmail LIKE CONCAT('%',#{searchKeyword,jdbcType=VARCHAR},'%' ) ) 
		     
			) as A
	group by searchDate;

select sql_calc_found_rows PA.paymentTransactionNo, PA.memberNo, PA.memberName, PA.memberPhone, PA.memberEmail, PA.nodeType, PA.nodeNo, PA.nodeEmail, PA.nodeName, PA.nodePhone, PA.affiliateNo, PA.affiliateSerial, AF.affiliateName, PA.paymentApprovalAmount, PA.paymentApprovalNumber, PA.paymentApprovalStatus, PA.pointBackStatus, PA.paymentTransactionType, PA.paymentApprovalDateTime, PA.orgPaymentData, PA.regAdminNo, PA.createTime, PA.updateTime, PTR.paymentRouterNo, (SELECT paymentRouterType from payment_router WHERE paymentRouterNo = PTR.paymentRouterNo) as paymentRouterType, (SELECT paymentRouterName from payment_router WHERE paymentRouterNo = PTR.paymentRouterNo) as paymentRouterName, (SELECT paymentRouterCode from payment_router WHERE paymentRouterNo = PTR.paymentRouterNo) as paymentRouterCode
from payment_transaction as PA inner join affiliate as AF on (PA.affiliateNo= AF.affiliateNo) inner join payment_transaction_router as PTR on (PA.paymentTransactionNo = PTR.paymentTransactionNo) where 1 = 1 AND PA.paymentApprovalDateTime LIKE CONCAT(#{searchDate},'%') AND PA.paymentTransactionType = #{searchPaymentTransactionType} AND PA.paymentApprovalStatus = #{searchPaymentApprovalStatus} AND PA.affiliateNo = #{searchAffiliate}
AND ( memberName LIKE CONCAT('%',#{searchKeyword,jdbcType=VARCHAR},'%' ) OR memberEmail LIKE CONCAT('%',#{searchKeyword,jdbcType=VARCHAR},'%' ) )
order by ${order}
order by createTime desc
limit ${pageSize} offset ${offset}

	select 
		date_format(A.paymentApprovalDateTime, '%Y-%m-%d') as searchDate,
   	 	sum(A.paymentApprovalAmount) as paySum,
		sum(A.realPaymentAmount) as realPaySum,
		sum(A.gpointPaymentAmount) as gpointPaySum,
	    count(A.realPaymentAmount) as payCase
	from 
		(
		 select * from gpoint_payment where 1= 1
			 and (paymentApprovalDateTime between #{searchDateStart} and #{searchDateEnd})
			and affiliateNo = #{searchAffiliate}
			and paymentTransactionType = #{searchPaymentTransactionType}
			and paymentApprovalStatus = #{searchPaymentApprovalStatus}
			and paymentMethod = #{searchPaymentMethod}
			) as A
	group by searchDate;

SELECT gpointPaymentNo, memberNo, memberName, memberEmail, memberPhone, affiliateNo, affiliateSerial, affiliateName paymentApprovalAmount, paymentApprovalNumber, paymentApprovalStatus, paymentMethod, paymentTransactionType, paymentApprovalDateTime, realPaymentAmount, gpointPaymentAmount createTime, updateTime FROM gpoint_payment where 1 = 1 AND paymentApprovalDateTime LIKE CONCAT(#{searchDate},'%') AND paymentTransactionType = #{searchPaymentTransactionType} AND paymentApprovalStatus = #{searchPaymentApprovalStatus} AND affiliateNo = #{searchAffiliate} and paymentMethod = #{searchPaymentMethod}
order by ${order}
order by createTime desc
limit ${pageSize} offset ${offset}
