mapper.delete:
	delete from affiliate_tid
	where 
		1 = 1 
		 AND affiliateTidNo = #{affiliateTidNo,jdbcType=INTEGER}
         AND affiliateNo = #{affiliateNo,jdbcType=INTEGER}
         AND tid = #{tid,jdbcType=VARCHAR}

	delete from green_point
	where 
		1 = 1 
		 AND memberNo = #{memberNo,jdbcType=INTEGER}
         AND nodeNo = #{nodeNo,jdbcType=INTEGER}
