mapper.select:
select sql_calc_found_rows PA.paymentTransactionNo, PA.memberNo, PA.memberName, PA.memberPhone, PA.memberEmail, PA.nodeType, PA.nodeNo, PA.nodeEmail, PA.nodeName, PA.nodePhone, PA.affiliateNo, PA.affiliateSerial, AF.affiliateName, PA.paymentApprovalAmount, PA.paymentApprovalNumber, PA.paymentApprovalStatus, PA.pointBackStatus, PA.paymentTransactionType, PA.paymentApprovalDateTime, PA.orgPaymentData, PA.regAdminNo, PA.createTime, PA.updateTime, PTR.paymentRouterNo, (SELECT paymentRouterType from payment_router WHERE paymentRouterNo = PTR.paymentRouterNo) as paymentRouterType, (SELECT paymentRouterName from payment_router WHERE paymentRouterNo = PTR.paymentRouterNo) as paymentRouterName, (SELECT paymentRouterCode from payment_router WHERE paymentRouterNo = PTR.paymentRouterNo) as paymentRouterCode from payment_transaction as PA inner join affiliate as AF on (PA.affiliateNo= AF.affiliateNo) inner join payment_transaction_router as PTR on (PA.paymentTransactionNo = PTR.paymentTransactionNo) where 1 = 1 AND PA.paymentApprovalDateTime BETWEEN #{searchDateStart} AND #{searchDateEnd}
order by ${order}
order by createTime desc
limit ${pageSize} offset ${offset}
